import unittest
from kata_solved.decodemorseadvanced.dec_morse_adv import decodeBits, decodeMorse


class MyTestCase(unittest.TestCase):
    def test_bits(self):
        self.assertEqual(decodeMorse(decodeBits(
            '111000111000111')),
                         'S')
        self.assertEqual(decodeMorse(decodeBits(
            '111110000011111')),
            'I')
        self.assertEqual(decodeMorse(decodeBits(
            '000000011100000')),
            'E')
        self.assertEqual(decodeMorse(decodeBits('1100110011001100000011000000111111001100111111001111110000000000000011001111110011111100111111000000110011001111110000001111110011001100000011')), 'HEY JUDE')
        self.assertEqual(decodeMorse(decodeBits(
            '')),
            'THE QUICK BROWN FOX JUMPS OVER THE LAZY DM')


if __name__ == '__main__':
    unittest.main()
